INSERT INTO `abouts` (`id`, `image`, `text_uz`, `text_oz`, `text_ru`, `text_en`, `videofile`, `history_uz`, `history_oz`, `history_ru`, `history_en`, `created_at`, `updated_at`, `head_text_uz`, `head_text_oz`, `head_text_ru`, `head_text_en`, `images`) VALUES
(1, 'abouts/May2023/y0xBQB8IVZFA6nUL0W4z.png', '<p style=\"text-align: center;\"><strong>Fondning ramzi</strong></p>\r\n<p style=\"text-align: left;\">\"Vatandoshlar\" jamoat fondining ramzida asosan milliy qadriyatlarimizni anglatuvchi minoralar hamda tarixiy obidalarimizning naqshlarida aks etgan ko&lsquo;k rangdan foydalanilgan.&nbsp;<br>Ko&lsquo;k rang &ndash; bu tinchlik va hotirjamlik ramzi bo&lsquo;lib, u yaxshilikni, donishmandlikni, halollikni va sadoqatni bildiradi.&nbsp;<br>Markazda O&lsquo;zbekiston Respublikasining 30 yilligi munosabati bilan &ldquo;Yangi O&lsquo;zbekiston&rdquo; bog&lsquo;ida bunyod etilgan muhtasham obida &ldquo;Mustaqillik monumenti&rdquo; joy olgan. Obidaning eng yuqori qismida O&lsquo;zbekiston gerbidan ham o&lsquo;rin olgan &ldquo;Humo qushi&rdquo; tasvirlangan. Obidaning orqa tomonida globus tasviri tushirilgan bo&lsquo;lib, bu xorijda istiqomat qilayotgan vatandoshlar bilan ishlashni nazarda tutadi.<br>Shuningdek, ramzda ochiq kaftlar keltirilgan bo&lsquo;lib, bu Fondning ochiqlik va qabul qilishlikni, halollik va samimiylikni, vatandoshlarni &ldquo;Yangi O&lsquo;zbekiston&rdquo; atrofida yanada jipslashishiga, ularga doimiy ravishda g&lsquo;amxo&lsquo;rlik qilishga ham tayyor ekanligini anglatadi.</p>', '<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: 42.55pt; mso-pagination: none; tab-stops: 51.75pt; background: white; mso-background-themecolor: background1;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 42.55pt; background: white; text-align: center;\"><strong><span class=\"word\">Фонднинг рамзи</span></strong></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 42.55pt; background: white; text-align: left;\"><span class=\"word\">\"Ватандошлар\"</span>&nbsp;<span class=\"word\">жамоат</span>&nbsp;<span class=\"word\">фондининг рамзи</span>да асосан миллий қадриятларимизни англатувчи миноралар ҳамда тарихий обидаларимизнинг нақшларида акс этган кўк рангдан фойдаланилган. Кўк ранг &ndash; бу тинчлик ва ҳотиржамлик рамзи бўлиб, у яхшиликни, донишмандликни, ҳалолликни ва садоқатни билдиради.&nbsp;<br>Марказда Ўзбекистон Республикасининг 30 йиллиги муносабати билан &ldquo;Янги Ўзбекистон&rdquo; боғида бунёд этилган муҳташам обида &ldquo;Мустақиллик монументи&rdquo; жой олган. Обиданинг энг юқори қисмида Ўзбекистон гербидан ҳам ўрин олган &ldquo;Ҳумо қуши&rdquo; тасвирланган. Обиданинг орқа томонида глобус тасвири туширилган бўлиб, бу хорижда истиқомат қилаётган ватандошлар билан ишлашни назарда тутади.<br>Шунингдек, рамзда очиқ кафтлар келтирилган бўлиб, бу Фонднинг очиқлик ва қабул қилишликни, ҳалоллик ва самимийликни, ватандошларни &ldquo;Янги Ўзбекистон&rdquo; атрофида янада жипслашишига, уларга доимий равишда ғамхўрлик қилишга ҳам тайёр эканлигини англатади.</p>', '<p>Дальнейшее разъединение соотечественников, проживающих за рубежом, вокруг своей исторической родины, повышение в их сердцах и умах чувства гордости за Родину, сохранение национального самосознания, взаимопонимание между соотечественниками и ими<br>Одной из основных целей Фонда является дальнейшее объединение соотечественников, проживающих за рубежом, вокруг своей исторической родины, повышение в их сердцах и умах чувства гордости за страну, сохранение национального самосознания, поддержка соотечественников и созданных ими общественных объединений, эффективное направление потенциала соотечественников, работающих в различных сферах, на развитие нашей страны. Одной из основных целей Фонда является дальнейшая интеграция соотечественников, проживающих за рубежом, в историческую родину, повышение в их сердцах и умах чувства гордости за Родину, сохранение национального самосознания, поддержка соотечественников и созданных ими общественных объединений, эффективное направление потенциала соотечественников, работающих в различных сферах, на развитие нашей страны.</p>', '<p>&nbsp; Further emboldening of compatriots living abroad around their historical homeland, exalting in their heart and mind a sense of pride in the land, maintaining a national identity, by compatriots and by them<br>One of the main goals of the Foundation is to further embed compatriots living abroad around their historical homeland, raise their sense of pride in the country in their hearts and minds, maintain national identity, support compatriots and public associations formed by them, and effectively direct the potential of our compatriots operating in various fields to the development of our country. One of the main goals of the Foundation is to further embed compatriots living abroad around their historical homeland, raise their sense of pride in the country in their hearts and minds, maintain national identity, support compatriots and public associations formed by them, and effectively direct the potential of our compatriots operating in various fields to the development of our country.</p>', '[{\"download_link\":\"abouts\\/April2023\\/eU5YuesBMXU3J2N1hdOF.mp4\",\"original_name\":\"slider_video.mp4\"}]', '<p><span class=\"word\">Fondning</span>&nbsp;<span class=\"word\">tashkil</span>&nbsp;<span class=\"word\">topishi</span>&nbsp;<span class=\"word\">haqida&nbsp;</span></p>\r\n<p><span class=\"word\">\"Vatandoshlar\"</span>&nbsp;<span class=\"word\">jamoat</span>&nbsp;<span class=\"word\">fondi&nbsp;</span>&nbsp;<span class=\"word\">2021</span>&nbsp;<span class=\"word\">yil</span>&nbsp;<span class=\"word\">11</span>&nbsp;<span class=\"word\">avgustda</span>&nbsp;<span class=\"word\">O&lsquo;zbekiston</span>&nbsp;<span class=\"word\">Respublikasi</span>&nbsp;<span class=\"word\">Prezidentining</span>&nbsp;<span class=\"word\">PQ-5220-sonli</span>&nbsp;<span class=\"word\">qarori</span>&nbsp;<span class=\"word\">bilan</span>&nbsp;<span class=\"word\">xorijiy</span>&nbsp;<span class=\"word\">davlatlardagi</span>&nbsp;<span class=\"word\">vatandoshlarimiz</span>&nbsp;<span class=\"word\">bilan</span>&nbsp;<span class=\"word\">doimiy</span>&nbsp;<span class=\"word\">va</span>&nbsp;<span class=\"word\">samarali</span>&nbsp;<span class=\"word\">aloqalar</span>&nbsp;<span class=\"word\">o&lsquo;rnatish</span>&nbsp;<span class=\"word\">va</span>&nbsp;<span class=\"word\">rivojlantirish</span>&nbsp;<span class=\"word\">hamda</span>&nbsp;<span class=\"word\">yagona</span>&nbsp;<span class=\"word\">Vatani</span>&nbsp;<span class=\"word\">atrofida</span>&nbsp;<span class=\"word\">jiplashtirish</span>&nbsp;<span class=\"word\">maqsadida</span>&nbsp;<span class=\"word\">tashkil</span>&nbsp;<span class=\"word\">etilgan.</span><br><br><span class=\"word\">\"Vatandoshlar\"</span>&nbsp;<span class=\"word\">jamoat</span>&nbsp;<span class=\"word\">fondining</span>&nbsp;<span class=\"word\">vasiylik</span>&nbsp;<span class=\"word\">kengashi</span>&nbsp;<span class=\"word\">Fondning</span>&nbsp;<span class=\"word\">oliy</span>&nbsp;<span class=\"word\">organi</span>&nbsp;<span class=\"word\">hisoblanadi.</span></p>\r\n<p style=\"text-align: left;\">&nbsp;<span class=\"word\">Vasiylik</span>&nbsp;<span class=\"word\">kengashi</span>&nbsp;<span class=\"word\">tomonidan</span>&nbsp;<span class=\"word\">shakllantiriladigan</span>&nbsp;<span class=\"word\">Fond</span>&nbsp;<span class=\"word\">Boshqaruvi</span>&nbsp;<span class=\"word\">&mdash;</span>&nbsp;<span class=\"word\">Fondning</span>&nbsp;<span class=\"word\">ijro</span>&nbsp;<span class=\"word\">organi</span>&nbsp;<span class=\"word\">hisoblananadi.</span><br><br><span class=\"word\">O&lsquo;zbekiston</span>&nbsp;<span class=\"word\">Respublikasi</span>&nbsp;<span class=\"word\">Oliy</span>&nbsp;<span class=\"word\">Majlisi</span>&nbsp;<span class=\"word\">huzuridagi</span>&nbsp;<span class=\"word\">Nodavlat</span>&nbsp;<span class=\"word\">notijorat</span>&nbsp;<span class=\"word\">tashkilotlarini</span>&nbsp;<span class=\"word\">va</span>&nbsp;<span class=\"word\">fuqarolik</span>&nbsp;<span class=\"word\">jamiyatining</span>&nbsp;<span class=\"word\">boshqa</span>&nbsp;<span class=\"word\">institutlarini</span>&nbsp;<span class=\"word\">qo&lsquo;llab -quvvatlash</span>&nbsp;<span class=\"word\">jamoat</span>&nbsp;<span class=\"word\">fondi</span>&nbsp;<span class=\"word\">mablag&lsquo;larini</span>&nbsp;<span class=\"word\">boshqarish</span>&nbsp;<span class=\"word\">bo&lsquo;yicha</span>&nbsp;<span class=\"word\">Parlament</span>&nbsp;<span class=\"word\">komissiyasiga</span>&nbsp;<span class=\"word\">har</span>&nbsp;<span class=\"word\">yili</span>&nbsp;<span class=\"word\">asoslangan</span>&nbsp;<span class=\"word\">buyurtmanoma</span>&nbsp;<span class=\"word\">asosida</span>&nbsp;</p>\r\n<p><span class=\"word\">Fondning</span>&nbsp;<span class=\"word\">asosiy</span>&nbsp;<span class=\"word\">vazifalarini</span>&nbsp;<span class=\"word\">bajarish</span>&nbsp;<span class=\"word\">doirasidagi</span>&nbsp;<span class=\"word\">tadbirlarni</span>&nbsp;<span class=\"word\">tashkil</span>&nbsp;<span class=\"word\">etish</span>&nbsp;<span class=\"word\">va</span>&nbsp;<span class=\"word\">o&lsquo;tkazishni</span>&nbsp;<span class=\"word\">moliyalashtirish</span>&nbsp;<span class=\"word\">uchun</span>&nbsp;<span class=\"word\">subsidiya</span>&nbsp;<span class=\"word\">shaklida</span>&nbsp;<span class=\"word\">zarur</span>&nbsp;<span class=\"word\">mablag&lsquo;larni&nbsp;</span>&nbsp;<span class=\"word\">ajratadi.</span></p>', '<p>Фонднинг ташкил топиши ҳақида&nbsp;</p>\r\n<p>\"Ватандошлар\" жамоат фонди &nbsp;2021 йил 11 августда Ўзбекистон Республикаси Президентининг ПҚ-5220-сонли қарори билан хорижий давлатлардаги ватандошларимиз билан доимий ва самарали алоқалар ўрнатиш ва ривожлантириш ҳамда ягона Ватани атрофида жиплаштириш мақсадида ташкил этилган.</p>\r\n<p>\"Ватандошлар\" жамоат фондининг васийлик кенгаши Фонднинг олий органи ҳисобланади. Васийлик кенгаши томонидан шакллантириладиган Фонд Бошқаруви &mdash; Фонднинг ижро органи ҳисоблананади.</p>\r\n<p>Ўзбекистон Республикаси Олий Мажлиси ҳузуридаги Нодавлат нотижорат ташкилотларини ва фуқаролик жамиятининг бошқа институтларини қўллаб-қувватлаш жамоат фонди маблағларини бошқариш бўйича Парламент комиссиясига ҳар йили асосланган буюртманома асосида Фонднинг асосий вазифаларини бажариш доирасидаги тадбирларни ташкил этиш ва ўтказишни молиялаштириш учун субсидия шаклида зарур маблағларни &nbsp;ажратади.&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify; text-indent: 42.55pt;\">&nbsp;</p>', '<p>О создании фонда</p>', '<p>About the foundation&nbsp;</p>', '2023-03-29 12:21:35', '2023-05-23 10:19:24', '<p>&ldquo;Vatandoshlar&rdquo; jamoat fondi 2021 yil 11 avgustda tashkil etilgan.<br>Bizning asosiy maqsadimiz- xorijda istiqomat qilayotgan vatandoshlarni tarixiy Vatani atrofida yanada jipslashtirish, ularning qalbi va ongida yurt bilan faxrlanish tuyg&lsquo;usini yuksaltirish, milliy o&lsquo;zlikni saqlab qolish, vatandoshlar va ular tomonidan tuzilgan jamoat birlashmalarini qo&lsquo;llab-quvvatlash, turli sohalarda faoliyat yuritayotgan vatandoshlarimizning salohiyatini mamlakatimiz taraqqiyotiga samarali yo&lsquo;naltirishdir!&nbsp;</p>', '<p>&ldquo;Ватандошлар&rdquo; жамоат фонди 2021 йил 11 августда ташкил этилган.<br>Бизнинг асосий мақсадимиз- хорижда истиқомат қилаётган ватандошларни тарихий Ватани атрофида янада жипслаштириш, уларнинг қалби ва онгида юрт билан фахрланиш туйғусини юксалтириш, миллий ўзликни сақлаб қолиш, ватандошлар ва улар томонидан тузилган жамоат бирлашмаларини қўллаб-қувватлаш, турли соҳаларда фаолият юритаётган ватандошларимизнинг салоҳиятини мамлакатимиз тараққиётига самарали йўналтиришдир!</p>', NULL, NULL, '[\"abouts\\/May2023\\/ous5jdZNntrQSnn7hp65.jpg\",\"abouts\\/May2023\\/zN2t5udxSTOBJnO2jFZ6.jpg\",\"abouts\\/May2023\\/f3jB09CzkIGeOjQHlb9W.jpg\",\"abouts\\/May2023\\/NOHy1AcmKpy5dXaI67lP.jpg\",\"abouts\\/May2023\\/ZXgnmMBAyz0fZPNpCSp9.jpg\",\"abouts\\/May2023\\/iQaPFQcB69umNImNLYVw.jpg\",\"abouts\\/May2023\\/7nyqzltep9x9m1JJQ5Dp.jpg\",\"abouts\\/May2023\\/KbdzE6jFDxJHfl9hVM05.jpg\",\"abouts\\/May2023\\/z9q5Cq46BSEyxHqyLQlZ.jpg\",\"abouts\\/May2023\\/4X8xtnqWib4XGeEFV133.jpg\",\"abouts\\/May2023\\/RaeUwC5qTI8K2vPnAmcf.jpg\",\"abouts\\/May2023\\/jmkehKfMlQCDEUlzd8FI.jpg\",\"abouts\\/May2023\\/1Ddlx8C2ZeOcLUdsm6mM.jpg\",\"abouts\\/May2023\\/YRF2gMCngnB1x69jl54O.jpg\",\"abouts\\/May2023\\/IbGJOdejN4l1ncgcsxif.jpg\",\"abouts\\/May2023\\/42bxMgIKXM6hbPl3JA72.jpg\",\"abouts\\/May2023\\/GhuoLsQU8CpapCxohwLQ.jpg\",\"abouts\\/May2023\\/J4UnJgtgTsDucJEHC7Fp.jpg\"]');
