INSERT INTO `sliders` (`id`, `image`, `video`, `title_uz`, `title_oz`, `title_ru`, `title_en`, `text_uz`, `text_oz`, `text_ru`, `text_en`, `created_at`, `updated_at`, `images`, `tag_uz`, `tag_oz`, `tag_ru`, `tag_en`, `viewers`, `data`) VALUES
(1, 'sliders/March2023/K0E0IS5DLSOB94afh7ov.png', '[]', 'Janubiy Koreyadagi vatandoshlarimiz', 'Жанубий Кореядаги ватандошларимиз', 'Наши соотечественники в Южной Корее', 'Our compatriots in South Korea', '<p>Toshkent shahrida joylashgan &ldquo;Daniel Hill Hotel&rdquo; mehmonxonasi ma&rsquo;muriyati O&lsquo;zbekiston hududiga chet eldan kelayotgan vatandoshlarimizni karantinda saqlash uchun yordam berishga shay ekanini ma&rsquo;lum qildi.</p>\r\n<p>Mehmonxona 350 kishi uchun (150 xona) mo&lsquo;ljallangan bo&lsquo;lib, unda karantin sharoitidagi yurtdoshlarimiz 3 mahal taom bilan siylanadi.</p>\r\n<p>&ldquo;Daniel Hill Hotel&rdquo; mehmonxonasi ma&rsquo;muriyati mamlakatdagi koronavirus bilan bog&lsquo;liq murakkab vaziyatni inobatga olib, chet ellik investorlar bilan kelishilgan holda, O&lsquo;zbekiston Respublikasi Sog&lsquo;liqni saqlash vazirligiga yordam berish va xizmat qilish uchun tayyor.</p>', '<p>Тошкент шаҳрида жойлашган &ldquo;Даниел Ҳилл Ҳотел&rdquo; меҳмонхонаси ма&rsquo;мурияти О&lsquo;збекистон ҳудудига чет елдан келаётган ватандошларимизни карантинда сақлаш учун ёрдам беришга шай еканини ма&rsquo;лум қилди.</p>\r\n<p>Меҳмонхона 350 киши учун (150 хона) мо&lsquo;лжалланган бо&lsquo;либ, унда карантин шароитидаги юртдошларимиз 3 маҳал таом билан сийланади.</p>\r\n<p>&ldquo;Даниел Ҳилл Ҳотел&rdquo; меҳмонхонаси ма&rsquo;мурияти мамлакатдаги коронавирус билан бог&lsquo;лиқ мураккаб вазиятни инобатга олиб, чет еллик инвесторлар билан келишилган ҳолда, О&lsquo;збекистон Республикаси Сог&lsquo;лиқни сақлаш вазирлигига ёрдам бериш ва хизмат қилиш учун тайёр.</p>', '<p>Администрация гостиницы \"Daniel Hill Hotel\", расположенной в городе Ташкент, заявила, что стоит помочь ввести карантин нашим соотечественникам, приезжающим на территорию Узбекистана из-за рубежа.</p>\r\n<p>Гостиница рассчитана на 350 человек (150 номеров), где нашим соотечественникам, находящимся на карантине, предоставляется 3-разовое питание.</p>\r\n<p>Администрация отеля Daniel Hill готова оказывать содействие и обслуживать Министерство здравоохранения Республики Узбекистан в координации с иностранными инвесторами, принимая во внимание сложную ситуацию с коронавирусом в стране.</p>', '<p>The administration of the Hotel \"Daniel Hill Hotel\", located in the city of Tashkent, said that it is worth helping to quarantine our compatriots coming to the territory of Uzbekistan from abroad.</p>\r\n<p>The hotel is designed for 350 people (150 rooms), where our compatriots in quarantine are treated to 3 meals.</p>\r\n<p>The administration of the Daniel Hill Hotel is ready to assist and serve the Ministry of health of the Republic of Uzbekistan, in coordination with foreign investors, taking into account the complex situation with the coronavirus in the country.</p>', '2023-03-17 09:34:30', '2023-05-18 08:58:47', '[\"sliders\\/April2023\\/EqnXb5WQMESsvd9reiOY.png\",\"sliders\\/April2023\\/jpdi47kC6gHLVaEr35bx.png\",\"sliders\\/April2023\\/3vmjgN9stqr4DgSkZIyv.png\",\"sliders\\/April2023\\/EOZA7XaLaQxi8fZnb97q.png\",\"sliders\\/April2023\\/snGHNRP5ua8S62JiOXGv.png\"]', NULL, NULL, NULL, NULL, '24', '2023-04-05'),
