INSERT INTO `webinars` (`id`, `title_uz`, `title_oz`, `title_ru`, `title_en`, `text_uz`, `text_oz`, `text_ru`, `text_en`, `background_image`, `created_at`, `updated_at`) VALUES
(2, '\"Vatandoshlar\" vebinari', '\"Ватандошлар\" вебинари', 'Вебинар \"Ватандошлар\"', 'Webinar \"Vatandoshlar\"', 'Vatandoshlar bilan aloqalaringizni mustahkamlang va ulardan qanday qilib maksimal darajada foydalanishni o\'rganing. Ushbu vebinarda siz umumiy maqsad va manfaatlarni aniqlash, tengdoshlar o\'rtasida ishonch va tushunishni shakllantirish, mazmunli munosabatlar o\'rnatishni o\'rganasiz.', 'Ватандошлар билан алоқаларингизни мустаҳкамланг ва улардан қандай қилиб максимал даражада фойдаланишни ўрганинг. Ушбу вебинарда сиз умумий мақсад ва манфаатларни аниқлаш, тенгдошлар ўртасида ишонч ва тушунишни шакллантириш, мазмунли муносабатлар ўрнатишни ўрганасиз.', 'Укрепляйте свои связи с другими людьми и изучайте, как извлечь из них максимальную пользу. На этом вебинаре вы узнаете, как определить общие цели и интересы, укрепить доверие и взаимопонимание между сверстниками и создать значимые отношения.', 'Strengthen your connections with others and explore how to get the most out of them. In this webinar, you will learn how to identify common goals and interests, build trust and understanding among peers, and create meaningful relationships.', 'webinars/May2023/0VOqAWtQxBTVboz47j10.png', '2023-05-08 09:24:28', '2023-05-08 09:24:28'),
(3, '\"Xorijda biznesni rivojlantirish\" vebinari', '\"Хорижда бизнесни ривожлантириш\" вебинари', 'Вебинар \"Развитие бизнеса за рубежом\"', 'Webinar \"Business Development Abroad\"', 'Ushbu vebinar xalqaro miqyosda biznesni tashkil etish va faoliyat yuritishdan manfaatdor bo\'lganlar uchun ma\'lumot va ko\'rsatmalar beradi.', 'Ушбу вебинар халқаро миқёсда бизнесни ташкил етиш ва фаолият юритишдан манфаатдор бўлганлар учун маълумот ва кўрсатмалар беради.', 'Этот вебинар предоставит информацию и рекомендации для тех, кто заинтересован в создании и ведении бизнеса на международном уровне.', 'This webinar will provide information and guidance for those interested in establishing and operating a business internationally.', 'webinars/May2023/58XMhhD2XhLYIi9fLp3y.png', '2023-05-08 09:29:44', '2023-05-08 09:29:44'),
(4, '\"Moliyaviy savodxonlik\" vebinari', '\"Молиявий саводхонлик\" вебинари', 'Вебинар\" финансовая грамотность\"', 'Webinar \"Financial literacy\"', 'Ushbu vebinar odamlarga byudjetlashtirish, tejash, investitsiya qilish va boshqa muhim moliyaviy tamoyillar kabi moliyaviy mavzularni yaxshiroq tushunishga yordam berish uchun mo\'ljallangan.', 'Ушбу вебинар одамларга бюджетлаштириш, тежаш, инвестиция қилиш ва бошқа муҳим молиявий тамойиллар каби молиявий мавзуларни яхшироқ тушунишга ёрдам бериш учун мўлжалланган.', 'Этот вебинар предназначен для того, чтобы помочь людям лучше понять финансовые темы, такие как составление бюджета, сбережения, инвестирование и другие важные финансовые принципы.', 'This webinar is designed to help people gain a better understanding of financial topics, such as budgeting, saving, investing, and other important financial principles.', 'webinars/May2023/DgnLLP8sI4lj1dJNkKpA.png', '2023-05-08 09:33:03', '2023-05-08 09:33:03');
